import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function ViewEmployee() {
  const { state } = useLocation();
  const { employee } = state || {};
  const navigate = useNavigate();

  if (!employee) {
    return <p>No employee data found.</p>;
  }

  return (
    <div>
      <h1>View Employee Details</h1>
      <div style={{ marginBottom: "20px" }}>
        <button onClick={() => navigate(-1)} style={{ marginRight: "10px" }}>
          Back
        </button>
        <button onClick={() => navigate("/employeeList")} style={{ marginLeft: "10px" }}>
          Home
        </button>
      </div>

      <div>
        <p>
          <strong>First Name:</strong> {employee.first_name}
        </p>
        <p>
          <strong>Last Name:</strong> {employee.last_name}
        </p>
        <p>
          <strong>Email:</strong> {employee.email}
        </p>
        <p>
          <strong>Position:</strong> {employee.position}
        </p>
        <p>
          <strong>Department:</strong> {employee.department}
        </p>
        <p>
          <strong>Salary:</strong> {employee.salary}
        </p>
        <p>
          <strong>Date of Joining:</strong>{" "}
          {new Date(employee.date_of_joining).toLocaleDateString()}
        </p>
      </div>
    </div>
  );
}
