import React from "react";
import { useLocation } from "react-router-dom";

export default function UpdateEmployee() {
  const { state } = useLocation();
  const { employee } = state || {}; // Extract the employee details from state

  if (!employee) {
    return <p>No employee data found for updating.</p>;
  }

  return (
    <div>
      <h1>Update Employee</h1>
      <form>
        <label>
          First Name:
          <input type="text" defaultValue={employee.first_name} />
        </label>
        <br />
        <label>
          Last Name:
          <input type="text" defaultValue={employee.last_name} />
        </label>
        <br />
        <label>
          Email:
          <input type="email" defaultValue={employee.email} />
        </label>
        <br />
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
}
