import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AddEmployee() {
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    position: "",
    salary: "",
    date_of_joining: "",
    department: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("accessToken");
      if (!token) {
        alert("Please login to add a new employee.");
        return;
      }

      const response = await fetch("http://localhost:3000/api/v1/emp/employees", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to create employee.");
      }

      alert("Employee added successfully!");
      navigate("/employeeList");
    } catch (error) {
      console.error("Error adding employee:", error);
      alert(`Failed to add employee: ${error.message}`);
    }
  };

  return (
    <div>
        <div style={{ marginBottom: "20px" }}>
        <button onClick={() => navigate(-1)} style={{ marginRight: "10px" }}>
          Back
        </button>
        <button onClick={() => navigate("/employeeList")} style={{ marginLeft: "10px" }}>
          Home
        </button>
      </div>
      <h1>Add New Employee</h1>
      <form onSubmit={handleSubmit}>
        <label>
          First Name:
          <input type="text" name="first_name" value={formData.first_name} onChange={handleChange} required />
        </label>
        <label>
          Last Name:
          <input type="text" name="last_name" value={formData.last_name} onChange={handleChange} required />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </label>
        <label>
          Position:
          <input type="text" name="position" value={formData.position} onChange={handleChange} required />
        </label>
        <label>
          Salary:
          <input type="number" name="salary" value={formData.salary} onChange={handleChange} required />
        </label>
        <label>
          Date of Joining:
          <input type="date" name="date_of_joining" value={formData.date_of_joining} onChange={handleChange} required />
        </label>
        <label>
          Department:
          <input type="text" name="department" value={formData.department} onChange={handleChange} required />
        </label>
        <button type="submit">Add Employee</button>
      </form>
    </div>
  );
}
