import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";


export default function EmployeeList() {
  const [employees, setEmployees] = useState([]);

  const navigate = useNavigate();


  useEffect(() => {
    getEmployees();
  }, []);

  const getEmployees = async () => {
    try {
      const token = localStorage.getItem("accessToken");

      if (!token) {
        console.error("No token found. Redirecting to login.");
        navigate("/login"); // Redirect to login if no token
        return;
      }

      const response = await fetch("http://localhost:3000/api/v1/emp/employees", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.status === 401) {
        console.error("Unauthorized. Redirecting to login.");
        navigate("/login"); // Redirect to login if unauthorized
        return;
      }

      if (!response.ok) {
        throw new Error(`Failed to fetch employees: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      setEmployees(data);
    } catch (error) {
      console.error("Error fetching employees:", error);
    }
  };


  const logout = () => {
    localStorage.clear();
    navigate("/");
  };

  const handleView = (employee) => {
    navigate("/view-employee", { state: { employee } });
  };  


  const handleUpdate = (employee) => {
    alert(`Updating employee: ${employee.first_name} ${employee.last_name}`);
  };

  const handleDelete = (employeeId) => {
    alert(`Deleting employee with ID: ${employeeId}`);
    // Here, you can add an API call to delete the employee
  };

  return (
    <div>
      <div className="flex flex-row gap-3 lg:px-12 lg:absolute lg:top-6 lg:right-12  xl:px-0 xl:top-6 xl:right-6">
              <Link to="/employees">
                <button className="text-slate-100 bg-sky-700 rounded-xl px-4 py-2 hover:bg-gray-700/50  text-xl ">
                  Add Employee
                </button>
              </Link>
              <Link to="/">
                <button
                  className="text-slate-700 bg-gray-700/30 rounded-xl px-4 py-2 
                hover:bg-gray-700/50 text-xl "
                  onClick={logout}
                >
                  Log-out
                </button>
              </Link>
            </div>

      <h1>Employee List</h1>
      {employees.length > 0 ? (
        <table border="1" style={{ borderCollapse: "collapse", width: "100%" }}>
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee, index) => (
              <tr key={index}>
                <td>{employee.first_name}</td>
                <td>{employee.last_name}</td>
                <td>{employee.email}</td>
                <td>
                <Link to={`/employees/${employee._id}/detail`}>
                          <button className="bg-gray-700/30 rounded-xl px-4 py-2 hover:bg-gray-700/50  sm:px-12">
                            {" "}
                            View{" "}
                          </button>
                        </Link>
                  <button onClick={() => handleUpdate(employee)}>Update</button>
                  <button onClick={() => handleDelete(employee.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No employees found.</p>
      )}

    </div>
  );
}
