import React, { useState, useNavigate } from 'react'
import axios from "axios";



export default function Signup() {
    const [username, setUsername] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [confirmedPassword, setConfirmedPassword] = useState("")

    const userInput = {
        username: username,
        email: email,
        password: password
    }

    const navigate = useNavigate("")

    const handleRegistration = async(e) => {
        e.preventDefault();
        const {password, confirmedPassword} = userInput;
        if (password === confirmedPassword) {
            const response = await axios.post(
                "https://101274382-comp-3123-assignment1.vercel.app/api/v1/user/signup",
                userInput
            );
            if (response.status === 201) {
                navigate("/login")
            } else {
                console.log("Registration failed")
            }
        } else {
            console.log("Passwords do not match")
        }

    }
  return (
    <div>
        <form onSubmit={handleRegistration}>
            <input type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)}/>
            <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)}/>
            <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)}/>
            <input type="password" placeholder="Confirm Password" onChange={(e) => setConfirmedPassword(e.target.value)}/>
            <button type="submit">Sign Up</button>
        </form>
    </div> 
  )
}
