import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function EmployeeList() {
  const [employees, setEmployees] = useState([]);
  const [employee, setEmployee] = useState(null); // To hold the selected employee's details
  const navigate = useNavigate(); // Hook for navigation

  // Fetch employees on component mount
  useEffect(() => {
    getEmployees();
  }, []);

  const getEmployees = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      if (!token) {
        console.error("No token found. Please login.");
        return;
      }

      const response = await fetch("http://localhost:3000/api/v1/emp/employees", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch employees: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      setEmployees(data);
    } catch (error) {
      console.error("Error fetching employees:", error);
    }
  };

  const handleView = (employee) => {
    const token = localStorage.getItem("accessToken");
    if (!token) {
      alert("Please login to update employee details.");
      return;
    }

    // Navigate to update employee screen with employee details
    navigate(`/view-employee/${employee._id}`, { state: { employee } });
  };

  const handleDelete = async (employeeId) => {
    try {
      const token = localStorage.getItem("accessToken");
      if (!token) {
        console.error("No token found. Please login.");
        return;
      }

      const response = await fetch(`http://localhost:3000/api/v1/emp/employees?eid=${employeeId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || `Failed to delete employee: ${response.status}`);
      }

      alert("Employee deleted successfully!");
      setEmployees(employees.filter((employee) => employee._id !== employeeId)); // Remove the deleted employee from the list
    } catch (error) {
      console.error("Error deleting employee:", error);
      alert(`Failed to delete employee: ${error.message}`);
    }
  };

  const handleUpdate = (employee) => {
    const token = localStorage.getItem("accessToken");
    if (!token) {
      alert("Please login to update employee details.");
      return;
    }

    // Navigate to update employee screen with employee details
    navigate(`/update-employee/${employee._id}`, { state: { employee } });
  };

  const handleCloseDetails = () => {
    setEmployee(null); // Clear the selected employee to close details modal
  };

  return (
    <>
      <div>
        <h1>Employee List</h1>
        {employees.length > 0 ? (
          <table border="1" style={{ borderCollapse: "collapse", width: "100%" }}>
            <thead>
              <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {employees.map((employee, index) => (
                <tr key={index}>
                  <td>{employee.first_name}</td>
                  <td>{employee.last_name}</td>
                  <td>{employee.email}</td>
                  <td>
                    <button onClick={() => handleView(employee)}>View Details</button>
                    <button onClick={() => handleDelete(employee._id)}>Delete</button>
                    <button onClick={() => handleUpdate(employee)}>Update</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No employees found.</p>
        )}
      </div>
    </>
  );
}
