import React, { useEffect, useState } from "react";

export default function EmployeeList() {
  const [employees, setEmployees] = useState([]);
  const [employee, setEmployee] = useState(null);

  useEffect(() => {
    getEmployees();
  }, []);

  const getEmployees = async () => {
    try {
      const token = localStorage.getItem("accessToken");

      if (!token) {
        console.error("No token found. Please login.");
        return;
      }

      const response = await fetch("http://localhost:3000/api/v1/emp/employees", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch employees: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      setEmployees(data);
    } catch (error) {
      console.error("Error fetching employees:", error);
    }
  };

  const handleView = (employee) => {
    alert(`Viewing employee: ${JSON.stringify(employee, null, 2)}`);
  };

  const handleUpdate = (employee) => {
    alert(`Updating employee: ${employee.first_name} ${employee.last_name}`);
  };

  const handleDelete = (employeeId) => {
    alert(`Deleting employee with ID: ${employeeId}`);
    // Here, you can add an API call to delete the employee
  };

  return (
    <div>
      <h1>Employee List</h1>
      {employees.length > 0 ? (
        <table border="1" style={{ borderCollapse: "collapse", width: "100%" }}>
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee, index) => (
              <tr key={index}>
                <td>{employee.first_name}</td>
                <td>{employee.last_name}</td>
                <td>{employee.email}</td>
                <td>
                <button onClick={() => setSelectedEmployee(employee)}>View Details</button>
                  <button onClick={() => handleUpdate(employee)}>Update</button>
                  <button onClick={() => handleDelete(employee.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No employees found.</p>
      )}
    </div>
  );
}
